-- Comprehensive Exercise Database Seed (Robust Version)
-- 141 exercises covering all equipment types, difficulty levels, and muscle groups
-- RevPilot Gym - Inclusive Fitness for Everyone
-- Uses TEMP TABLE to safely UPSERT without breaking Foreign Keys

CREATE TEMP TABLE exercises_seed (
    name text,
    muscle_group text,
    equipment_type text[],
    difficulty text,
    rationale text
);


UPDATE exercises e
SET 
    equipment_type = s.equipment_type, 
    difficulty = s.difficulty, 
    rationale = s.rationale,
    muscle_group = s.muscle_group
FROM exercises_seed s
WHERE e.name = s.name;

SELECT name, muscle_group, equipment_type, difficulty, rationale 
FROM exercises_seed s
WHERE NOT EXISTS (SELECT 1 FROM exercises e WHERE e.name = s.name);

DROP TABLE exercises_seed;
